sys_script_include.script:
var IntegrateNowUtil = Class.create();
IntegrateNowUtil.prototype = {
    initialize: function () {
    },

    evaluateParameterScript: function (current, webServiceMap) {

        if (!current || !webServiceMap) { return null; }

        var evaluator = new GlideScopedEvaluator();
        var evalVar = {
            current: current,
            webServiceMap: webServiceMap,
            answer: null
        };

        evaluator.evaluateScript(webServiceMap, 'parameter_script', evalVar);

        var answer = evaluator.getVariable('answer');
        return answer;
    },

    removeHTMLTags: function (str) {
        var regX = /(<([^>]+)>)/ig;
        return str.replace(regX, "");
    },

    // === Value Checks ===

    isFunction: function (obj) {
        return !!(obj && obj.constructor && obj.call && obj.apply);
    },

    isArray: function (val) {
        return Object.prototype.toString.call(val) === '[object Array]';
    },

    isObject: function(val) {
        return (val !== null && typeof val === 'object');
    },

    // === Log ===

    log: function (msg, logType) {
        if (this.debug === false) {
            return null;
        } // exit

        logType = logType || 'info';

        if (!msg) {
            gs.error(msg + '');
            return null;
        }

        if (typeof msg === 'object' || this.INTUTIL._isArray(msg)) {
            msg = JSON.stringify(msg);
        }

        gs[logType](msg);
    },

    type: 'IntegrateNowUtil'
};
